package Introduction;

import java.util.Scanner;

public class Loops1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		 Scanner in = new Scanner(System.in);
	     int N = in.nextInt();
	     for (int i=1;i<11;i++){
	    	 System.out.println(N +" x " + i + " = " + (N * i));
	     }
	}

}
